const bugDates = queries.bugStats.data.allBugs.map((row) =>
  moment.unix(row.created_at).format("MM/YYYY")
);
let monthWiseCountIndex = {};
let chartData = [];

bugDates.forEach((row) => {
  if (monthWiseCountIndex.hasOwnProperty(row)) {
    chartData[monthWiseCountIndex[row]]["y"] += 1;
  } else {
    monthWiseCountIndex[row] = chartData.length;
    chartData.push({ x: row, y: 1 });
  }
});

return chartData;

